
for(var buy=document.querySelectorAll(".btn-buy"),cart=document.querySelector(".modal-cart"),close=cart.querySelector(".modal-close"),i=0;i<buy.length;i++)buy[i].addEventListener("click",function(e){event.preventDefault(),cart.classList.add("modal-show")});close.addEventListener("click",function(e){e.preventDefault(),cart.classList.remove("modal-show")});var link=document.querySelector(".message-link"),popup=document.querySelector(".modal"),form=(close=popup.querySelector(".modal-close"),popup.querySelector("form")),name=popup.querySelector("[name=name]"),email=popup.querySelector("[name=email]"),isStorageSupport=!0,storage="";try{storage=localStorage.getItem("name")}catch(e){isStorageSupport=!1}link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),storage?(name.value=storage,email.focus()):name.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),form.addEventListener("submit",function(e){name.value&&email.value?isStorageSupport&&localStorage.setItem("name",name.value):(e.preventDefault(),console.log("Введите имя и адрес электронной почты"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&popup.classList.remove("modal-show"))});var mapLink=document.querySelector(".show-map"),mapPopup=document.querySelector(".modal-map"),mapClose=mapPopup.querySelector(".modal-close");mapLink.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.add("modal-show")}),mapClose.addEventListener("click",function(e){e.preventDefault(),mapPopup.classList.remove("modal-show")});
